  res[i] <- cvv[cells, j]
}
i <- rs > 0 & rs < 3
if (sum(i) > 0) {
  vv <- v[i, , drop = FALSE]
  vv[is.na(vv[, 1]), 1] <- vv[is.na(vv[, 1]),
    2]
  vv[is.na(vv[, 2]), 2] <- vv[is.na(vv[, 2]),
    1]
  vv[is.na(vv[, 3]), 3] <- vv[is.na(vv[, 3]),
    4]
  vv[is.na(vv[, 4]), 4] <- vv[is.na(vv[, 4]),
    3]
  vmean <- rep(rowMeans(vv, na.rm = TRUE), 4)
  vv[is.na(vv)] <- vmean[is.na(vv)]
  res[i] <- .doBilinear(xyCoords[i, , drop = FALSE],
    x[, i, drop = FALSE], y[, i, drop = FALSE],
    vv)
}
i <- rs == 0
if (sum(i) > 0) {
  res[i] <- .doBilinear(xyCoords[i, , drop = FALSE],
    x[, i, drop = FALSE], y[, i, drop = FALSE],
    v[i, , drop = FALSE])
}


